# https://leetcode.com/problems/word-search-ii/


class Solution:
    def __init__(self):
        self.END_OF_WORD = '#' # tire数的终止标识符
        # 表示上下左右四个方向
        self.dx = [-1, 1, 0, 0]
        self.dy = [0, 0, -1, 1]

    def findWords(self, board: list, words: list) -> list:
        # 边界条件
        if not board or not board[0]:
            return []
        if not words:
            return []

        self.result = set() # 存储结果

        # 用字典结构手动构造tire树
        root = {}
        for word in words:
            node = root
            for c in word:
                node = node.setdefault(c, {})
            node[self.END_OF_WORD] = self.END_OF_WORD # 当前子树终止

        # 遍历boards的每个位置
        for i in range(len(board)):
            for j in range(len(board[0])):
                if board[i][j] in root: # 如果board的（i,j)位置的元素在tire树里（是公共前缀）
                    self._dfs(board, i, j, '', root)

        return self.result

    def _dfs(self, board, i, j, cur_word, cur_dict):
        '''

        :param board: 所有字母表
        :param i:  字母表的横坐标
        :param j:  字母表的纵坐标
        :param cur_word:  当前查询到的word（str)
        :param cur_dict:  trie数的当前字典（不用从根节点开始查询）
        :return:
        '''
        cur_word += board[i][j]  # 查询到当前位置时得到的字符串
        cur_dict = cur_dict[board[i][j]]  # 指定到trie数的当前层（即移动到下一层）

        if self.END_OF_WORD in cur_dict:  # 如果此时已经遍历到终止标识符，说明该字符串存在与board中
            self.result.add(cur_word)

        tmp, board[i][j] = board[i][j], '@'  # 中间变量，记录下当前位置的元素； 同时用‘@’字符修改原位置的元素，表示当前位置已经访问过
        for k in range(4):  # board的上下左右四个位置
            x, y = i + self.dx[k], j + self.dy[k]
            if 0 <= x < len(board) and 0 <= y < len(board[0]) \
                    and board[x][y] != '@' \
                    and board[x][y] in cur_dict:
                # 新坐标没有越界
                # 新坐标的元素没有访问过
                # 新坐标的元素也在tire树里

                # 则继续深度优先搜索
                self._dfs(board, x, y, cur_word, cur_dict)
        # 搜索完成后，再把原位置复原（之前修改为“@”标识符，表示已经访问过）
        board[i][j] = tmp

# 只使用深度优先搜索
class Solution_2:
    def __init__(self):
        # 表示上下左右四个方向
        self.dx = [-1, 1, 0, 0]
        self.dy = [0, 0, -1, 1]

        # 存储结果
        self.result = set()

    def findWords(self, board: list, words: list) -> list:
        # 深度优先搜索， 遍历board的行和列
        for word in words:
            for i in range(len(board)):
                for j in range(len(board[0])):
                    # 为每一个字符在board里进行深度优先搜索
                    self._dfs(board, i, j, 0, word) # 从第0个字符开始，在board里搜索word的每一个字符

        return self.result


    def _dfs(self, board, i, j, char_index, word):
        '''
        :param board:
        :param i:
        :param j:
        :param char_index:  查找到word的第char_index个字符
        :param word:  待查找的字符串
        :return:
        '''
        # 如果当前字符不相等，就没必要继续递归、比较后续的字符
        if board[i][j] != word[char_index]:
            return

        # 如果已经查找完word中的所有字符
        char_index += 1
        if char_index == len(word):
            self.result.add(word)
            return  # 找到一个单词，逐层递归返回、复原board

        tmp, board[i][j] = board[i][j], '@' # 临时变量存储当前位置的元素； 修改当前位置，表示该位置已经访问过
        for k in range(4):
            x, y = i + self.dx[k], j + self.dy[k]
            if 0<= x < len(board)  and 0<= y < len(board[0]) \
                and board[x][y] != '@' \
                and board[x][y] == word[char_index]:
                if not self._dfs(board, x, y, char_index, word):
                    pass
        board[i][j] = tmp # 复原board


