# 只使用深度优先搜索
# 额外创建一个visited数组，标识已经访问过该位置，避免找到一个单词后、逐层递归还原所花费的时间
class Solution_3(object):
    def __init__(self):
        self.set = None
        self.ans = set()  # set to remove duplicates
        self.visited = None

    def dfs(self, i, j, string, m, n, board):
        # boundary conditions
        if i < 0 or j < 0 or i >= m or j >= n or self.visited[i][j] == 1:
            return

            # marking visited to remove cycle
        self.visited[i][j] = 1

        # checks if the words in set/list, then puts it But we continue exploring. Trie reduces this exploration
        if string + board[i][j] in self.set:
            self.ans.add(string + board[i][j])

        # do for dfs
        self.dfs(i - 1, j, string + board[i][j], m, n, board)
        self.dfs(i + 1, j, string + board[i][j], m, n, board)
        self.dfs(i, j + 1, string + board[i][j], m, n, board)
        self.dfs(i, j - 1, string + board[i][j], m, n, board)

        # unmarking visited
        self.visited[i][j] = 0

    def findWords(self, board, words):
        # created set for fast lookup

        self.set = set(words)
        m = len(board);
        n = len(board[0])
        self.visited = [[0] * n for i in range(m)]

        # doing normal dfs
        for i in range(m):
            for j in range(n):
                self.dfs(i, j, "", m, n, board)

        # returning set as a list
        return list(self.ans)


